def get_journal(disorder_type):
    import random
    # Hata olan satırları düzeltin - tüm metinlerin arasına virgül ekleyin
    templates = JOURNAL_TEMPLATES.get(disorder_type, JOURNAL_TEMPLATES["Normal"])
    return random.choice(templates)
